import{_ as a,o as p,c as i,a as r}from"./app.85e154b6.js";var e="/images/\u9A6C\u4E01\u798F\u52D2.png",l="/images/\u5927\u5C0F\u9910\u5385\u5BF9\u6BD4.png";const n={},d=r('<h1 id="\u670D\u52A1\u5668\u7AEF\u9879\u76EE\u6F14\u8FDB" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u5668\u7AEF\u9879\u76EE\u6F14\u8FDB" aria-hidden="true">#</a> \u670D\u52A1\u5668\u7AEF\u9879\u76EE\u6F14\u8FDB</h1><h2 id="\u670D\u52A1\u5668\u521D\u671F\u72B6\u6001" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u5668\u521D\u671F\u72B6\u6001" aria-hidden="true">#</a> <strong>\u670D\u52A1\u5668\u521D\u671F\u72B6\u6001</strong></h2><p>\u6700\u65E9\u7684\u670D\u52A1\u5668\u5C31\u662F\u5B89\u88C5\u90E8\u7F72\u4E86\u4E00\u4E9B\u9759\u6001\u9875\u9762</p><p>\u529F\u80FD\u975E\u5E38\u5355\u4E00,\u53EA\u80FD\u505A\u4FE1\u606F\u7684\u5448\u73B0\u548C\u8F93\u51FA</p><h2 id="\u670D\u52A1\u5668\u52A8\u6001\u9875\u9762" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u5668\u52A8\u6001\u9875\u9762" aria-hidden="true">#</a> <strong>\u670D\u52A1\u5668\u52A8\u6001\u9875\u9762</strong></h2><p>\u540E\u6765\u56E0\u4E3A\u4E1A\u52A1\u548C\u6280\u672F\u7684\u53D1\u5C55,\u9875\u9762\u8FDE\u63A5\u4E86\u6570\u636E\u5E93,\u9875\u9762\u4E2D\u5927\u90E8\u5206\u6570\u636E\u6765\u81EA\u4E8E\u6570\u636E\u5E93\u4E2D</p><p>\u7528\u6237\u7684\u64CD\u4F5C\u53EF\u4EE5\u4FEE\u6539\u6570\u636E\u5E93\u7684\u5185\u5BB9,\u8FD9\u6837\u5728\u4E0D\u4FEE\u6539\u9875\u9762\u4EE3\u7801\u7684\u524D\u63D0\u4E0B,\u9875\u9762\u4E2D\u7684\u6570\u636E\u53EF\u80FD\u53D1\u751F\u53D8\u5316</p><p>\u8FD9\u6837\u7684\u9875\u9762\u53EF\u4EE5\u5B9E\u73B0\u7528\u6237\u6CE8\u518C,\u767B\u5F55,\u67E5\u8BE2(\u57FA\u672C\u589E\u5220\u6539\u67E5)\u529F\u80FD</p><h2 id="\u7528\u6237\u8D21\u732E\u7F51\u9875\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u8D21\u732E\u7F51\u9875\u5185\u5BB9" aria-hidden="true">#</a> <strong>\u7528\u6237\u8D21\u732E\u7F51\u9875\u5185\u5BB9</strong></h2><p>\u8FDB\u516521\u4E16\u7EAA,\u968F\u7740\u4E92\u8054\u7F51\u8FDB\u5165\u5343\u5BB6\u4E07\u6237,\u4E2A\u4EBA\u591A\u793E\u4EA4\u9700\u6C42\u7684\u63D0\u5347</p><p>\u51FA\u73B0\u4E86\u5F88\u591A\u4E3B\u8981\u7531\u7528\u6237\u8D21\u732E\u4FE1\u606F\u7684\u7F51\u7AD9</p><p>\u73B0\u5728\u4E5F\u6709\u5F88\u591A\u8FD9\u6837\u7684\u7F51\u7AD9</p><p>\u5FAE\u535A,\u6296\u97F3,\u6DD8\u5B9D,\u5927\u4F17\u70B9\u8BC4\u6216\u7C7B\u4F3C\u7684\u7F51\u7AD9</p><h2 id="\u5FAE\u670D\u52A1\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1\u65F6\u4EE3" aria-hidden="true">#</a> <strong>\u5FAE\u670D\u52A1\u65F6\u4EE3</strong></h2><p>\u968F\u7740\u7528\u6237\u91CF\u7684\u589E\u52A0,\u548C\u5404\u79CD\u65B0\u9700\u6C42\u7684\u51FA\u73B0,\u4E00\u4E2A\u670D\u52A1\u5668\u5E94\u7528\u7A0B\u5E8F\u4E0D\u80FD\u652F\u6301\u7F51\u7AD9\u7684\u8FD0\u884C\u65F6</p><p>\u5C31\u9700\u8981\u591A\u53F0\u670D\u52A1\u5668\u5171\u540C\u652F\u6301\u7F51\u7AD9\u7684\u8FD0\u884C</p><p>\u6211\u4EEC\u9700\u8981\u5F3A\u5927\u7684\u670D\u52A1\u5668\u6027\u80FD,\u652F\u6301\u5404\u79CD\u7F51\u7AD9\u7684\u6D3B\u52A8</p><p>\u8FBE\u5230&quot;\u4E09\u9AD8&quot;\u7684\u76EE\u6807</p><p><strong>&quot;\u9AD8\u5E76\u53D1,\u9AD8\u53EF\u7528,\u9AD8\u6027\u80FD&quot;</strong></p><h1 id="java\u670D\u52A1\u5668\u9879\u76EE\u5206\u7C7B" tabindex="-1"><a class="header-anchor" href="#java\u670D\u52A1\u5668\u9879\u76EE\u5206\u7C7B" aria-hidden="true">#</a> Java\u670D\u52A1\u5668\u9879\u76EE\u5206\u7C7B</h1><p>\u73B0\u5728\u5E02\u9762\u4E0A\u5E38\u89C1\u7684java\u5F00\u53D1\u7684\u9879\u76EE\u53EF\u4EE5\u5206\u4E3A\u4E24\u5927\u7C7B</p><ol><li>\u4F01\u4E1A\u7EA7\u5E94\u7528</li></ol><p>\u4E00\u822C\u6307\u4E00\u4E2A\u4F01\u4E1A\u6216\u673A\u6784\u5185\u90E8\u4F7F\u7528\u7684\u7F51\u7AD9\u6216\u670D\u52A1\u5668\u5E94\u7528\u7A0B\u5E8F</p><p>\u5305\u62EC\u7684\u9886\u57DF\u5F88\u591A,\u5305\u62EC\u5E76\u4E0D\u9650\u4E8E:\u5546\u4E1A,\u4F01\u4E8B\u4E1A\u5355\u4F4D,\u533B\u7597,\u519B\u4E8B,\u653F\u5E9C,\u91D1\u878D\u7B49</p><p>\u8FD9\u79CD\u9879\u76EE\u7684\u7279\u5F81\u662F\u8BBF\u95EE\u4EBA\u6570\u4E0D\u591A,\u4E0D\u662F\u5168\u56FD\u4E43\u81F3\u5168\u4E16\u754C\u90FD\u9700\u8981\u4F7F\u7528\u7684\u7F51\u7AD9</p><p>\u56E0\u4E3A\u4EBA\u6570\u6BD4\u8F83\u5C11\u53C8\u6CA1\u6709\u66FF\u4EE3\u54C1,\u6240\u4EE5\u4E0D\u5F3A\u70C8\u8981\u6C42\u4E09\u9AD8</p><p>\u4F46\u662F\u4F01\u4E1A\u7EA7\u9879\u76EE\u4E00\u822C\u4F1A\u6709\u6BD4\u8F83\u590D\u6742\u7684\u6743\u9650\u8BBE\u7F6E\u548C\u4E1A\u52A1\u6D41\u7A0B</p><ol start="2"><li>\u4E92\u8054\u7F51\u5E94\u7528</li></ol><p>\u6307\u5BF9\u5168\u56FD\u4E43\u81F3\u5168\u4E16\u754C\u5F00\u653E\u7684\u7F51\u7AD9\u6216\u670D\u52A1\u5668\u5F15\u7528\u7A0B\u5E8F</p><p>\u6211\u4EEC\u624B\u673A\u4E2D\u5B89\u88C5\u7684app\u5927\u90E8\u5206\u90FD\u662F\u4E92\u8054\u7F51\u5E94\u7528</p><p>\u5FAE\u4FE1,\u652F\u4ED8\u5B9D,\u4EAC\u4E1C,\u6DD8\u5B9D,\u997F\u4E86\u4E48,\u7F8E\u56E2,\u6296\u97F3,qq\u97F3\u4E50,\u7231\u5947\u827A,\u9AD8\u5FB7\u5730\u56FE\u7B49</p><p>\u4ED6\u4EEC\u56E0\u4E3A\u5546\u4E1A\u7ADE\u4E89\u7B49\u5404\u79CD\u539F\u56E0,\u5BF9\u670D\u52A1\u5668\u6027\u80FD\u6709\u8981\u6C42,\u4E5F\u5C31\u662F\u524D\u9762\u63D0\u5230\u7684&quot;\u4E09\u9AD8&quot;</p><p>\u53CA\u65F6\u5E76\u53D1\u91CF\u975E\u5E38\u5927,\u7A0B\u5E8F\u4E5F\u8981\u6B63\u5E38\u8FD0\u884C,\u8FC5\u901F\u54CD\u5E94</p><p>\u4E92\u8054\u7F51\u5E94\u7528\u4E00\u822C\u4E1A\u52A1\u548C\u6743\u9650\u76F8\u5BF9\u7B80\u5355,\u4F46\u662F\u8981\u6C42&quot;\u4E09\u9AD8&quot;</p><p>\u56E0\u4E3A\u4E0A\u8FF0java\u9879\u76EE\u7684\u5206\u7C7B\u504F\u91CD\u70B9\u4E0D\u540C</p><p>\u5728\u5F53\u4ECAjava\u5F00\u53D1\u7684\u4E1A\u754C\u4E2D,\u6709\u4E0B\u9762\u7684\u57FA\u672C\u89C4\u5F8B</p><ul><li>\u5982\u679C\u5F00\u53D1\u7684\u662F\u4F01\u4E1A\u7EA7\u5E94\u7528,\u4F7F\u7528\u5355\u4F53\u67B6\u6784\u7684\u60C5\u51B5\u6BD4\u8F83\u591A</li><li>\u5982\u679C\u5F00\u53D1\u7684\u662F\u4E92\u8054\u7F51\u5E94\u7528,\u4F7F\u7528\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u60C5\u51B5\u6BD4\u8F83\u591A</li></ul><h1 id="\u5FAE\u670D\u52A1\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1\u6982\u8FF0" aria-hidden="true">#</a> \u5FAE\u670D\u52A1\u6982\u8FF0</h1><h2 id="\u4EC0\u4E48\u662F\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u5FAE\u670D\u52A1" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u5FAE\u670D\u52A1</h2><p>\u5FAE\u670D\u52A1\u7684\u6982\u5FF5\u662F\u7531Martin Fowler\uFF08<strong>\u9A6C\u4E01\xB7\u798F\u52D2</strong>\uFF09\u57282014\u5E74\u63D0\u51FA\u7684</p><p><img src="'+e+'" alt="\u9A6C\u4E01\u798F\u52D2.png"></p><p>\u5FAE\u670D\u52A1\u662F\u7531\u4EE5\u5355\u4E00\u5E94\u7528\u7A0B\u5E8F\u6784\u6210\u7684\u5C0F\u670D\u52A1\uFF0C\u81EA\u5DF1\u62E5\u6709\u81EA\u5DF1\u7684\u884C\u7A0B\u4E0E\u8F7B\u91CF\u5316\u5904\u7406\uFF0C\u670D\u52A1\u4F9D\u4E1A\u52A1\u529F\u80FD\u8BBE\u8BA1\uFF0C\u4EE5\u5168\u81EA\u52A8\u7684\u65B9\u5F0F\u90E8\u7F72\uFF0C\u4E0E\u5176\u4ED6\u670D\u52A1\u4F7F\u7528 HTTP API \u901A\u4FE1\u3002\u540C\u65F6\u670D\u52A1\u4F1A\u4F7F\u7528\u6700\u5C0F\u7684\u89C4\u6A21\u7684\u96C6\u4E2D\u7BA1\u7406\u80FD\u529B\uFF0C\u670D\u52A1\u53EF\u4EE5\u7528\u4E0D\u540C\u7684\u7F16\u7A0B\u8BED\u8A00\u4E0E\u6570\u636E\u5E93\u7B49\u7EC4\u4EF6\u5B9E\u73B0\u3002</p><p>\u7B80\u5355\u6765\u8BF4,\u5FAE\u670D\u52A1\u5C31\u662F\u5C06\u4E00\u4E2A\u5927\u578B\u9879\u76EE\u7684\u5404\u4E2A\u4E1A\u52A1\u4EE3\u7801,\u62C6\u5206\u6210\u591A\u4E2A\u4E92\u4E0D\u5E72\u6270\u7684\u5C0F\u9879\u76EE,\u800C\u8FD9\u4E9B\u5C0F\u9879\u76EE\u4E13\u5FC3\u5B8C\u6210\u81EA\u5DF1\u7684\u529F\u80FD,\u800C\u4E14\u53EF\u4EE5\u8C03\u7528\u522B\u7684\u5C0F\u9879\u76EE\u7684\u65B9\u6CD5,\u4ECE\u800C\u5B8C\u6210\u6574\u4F53\u529F\u80FD</p><p>\u4EAC\u4E1C\\\u6DD8\u5B9D\u8FD9\u6837\u7684\u5927\u578B\u4E92\u8054\u7F51\u5E94\u7528\u7A0B\u5E8F,\u57FA\u672C\u6BCF\u4E2A\u64CD\u4F5C\u90FD\u662F\u4E00\u4E2A\u5355\u72EC\u7684\u5FAE\u670D\u52A1\u5728\u652F\u6301:</p><ul><li>\u767B\u5F55\u670D\u52A1\u5668</li><li>\u641C\u7D22\u670D\u52A1\u5668</li><li>\u5546\u54C1\u4FE1\u606F\u670D\u52A1\u5668</li><li>\u8D2D\u7269\u8F66\u670D\u52A1\u5668</li><li>\u8BA2\u5355\u670D\u52A1\u5668</li><li>\u652F\u4ED8\u670D\u52A1\u5668</li><li>\u7269\u6D41\u670D\u52A1\u5668</li><li>.....</li></ul><h2 id="\u4E3A\u4EC0\u4E48\u4F7F\u7528\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4F7F\u7528\u5FAE\u670D\u52A1" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u4F7F\u7528\u5FAE\u670D\u52A1</h2><p><img src="'+l+'" alt="\u5927\u5C0F\u9910\u5385\u5BF9\u6BD4.png"></p><p>\u5DE6\u4FA7\u5C0F\u9910\u9986\u5C31\u50CF\u5355\u4F53\u9879\u76EE</p><p>\u4E00\u65E6\u670D\u52A1\u5668\u5FD9,\u6240\u6709\u4E1A\u52A1\u90FD\u65E0\u6CD5\u5FEB\u901F\u54CD\u5E94</p><p>\u5373\u4F7F\u6DFB\u52A0\u4E86\u670D\u52A1\u5668,\u4E5F\u4E0D\u80FD\u5F88\u597D\u7684\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898</p><p>\u4E0D\u80FD\u5F88\u597D\u7684\u5B9E\u73B0&quot;\u9AD8\u5E76\u53D1,\u9AD8\u53EF\u7528,\u9AD8\u6027\u80FD&quot;</p><p>\u4F46\u662F\u56E0\u4E3A\u670D\u52A1\u5668\u6570\u91CF\u5C11,\u6240\u4EE5\u6210\u672C\u4F4E,\u9002\u5408\u5E76\u53D1\u8BBF\u95EE\u5C11\u7684\u9879\u76EE</p><p>\u53F3\u4FA7\u7684\u5927\u9910\u5385\u5C31\u662F\u5FAE\u670D\u52A1\u9879\u76EE</p><p>\u6BCF\u4E2A\u4E1A\u52A1\u6709\u4E13\u95E8\u4E00\u6279\u4EBA\u8D1F\u8D23,\u4E1A\u52A1\u4E4B\u95F4\u4E92\u4E0D\u5F71\u54CD</p><p>\u80FD\u591F\u9488\u5BF9\u7684\u5728\u6027\u80FD\u4E0D\u8DB3\u7684\u4E1A\u52A1\u4E0A\u6DFB\u52A0\u670D\u52A1\u5668\u6765\u6539\u5584\u6027\u80FD,\u540C\u65F6\u4E07\u4E00\u4E00\u4E2A\u670D\u52A1\u5668\u5F02\u5E38,\u4E0D\u4F1A\u5F71\u54CD\u6574\u4F53\u529F\u80FD</p><p>\u4F46\u662F\u670D\u52A1\u5668\u6570\u91CF\u591A,\u6210\u672C\u9AD8,\u9002\u5408\u8981\u8981\u6C42&quot;\u9AD8\u5E76\u53D1,\u9AD8\u53EF\u7528,\u9AD8\u6027\u80FD&quot;\u7684\u9879\u76EE</p><h2 id="\u600E\u4E48\u642D\u5EFA\u5FAE\u670D\u52A1\u9879\u76EE" tabindex="-1"><a class="header-anchor" href="#\u600E\u4E48\u642D\u5EFA\u5FAE\u670D\u52A1\u9879\u76EE" aria-hidden="true">#</a> \u600E\u4E48\u642D\u5EFA\u5FAE\u670D\u52A1\u9879\u76EE</h2><p>\u5728\u5FAE\u670D\u52A1\u6982\u5FF5\u63D0\u51FA\u4E4B\u524D(2014\u5E74),\u6BCF\u4E2A\u5382\u5546\u90FD\u6709\u81EA\u5DF1\u7684\u89E3\u51B3\u65B9\u6848</p><p>\u4F46\u662FMartin Fowler\uFF08<strong>\u9A6C\u4E01\xB7\u798F\u52D2</strong>\uFF09\u63D0\u51FA\u4E86\u5FAE\u670D\u52A1\u7684\u6807\u51C6\u4E4B\u540E,\u4E3A\u4E86\u6280\u672F\u7EDF\u4E00\u548C\u517C\u5BB9\u6027,\u5F88\u591A\u4F01\u4E1A\u5F00\u59CB\u652F\u6301\u8FD9\u4E2A\u6807\u51C6</p><p>\u73B0\u5728\u4E1A\u754C\u4E2D\u5F00\u53D1\u5FAE\u670D\u52A1\u9879\u76EE,\u5927\u591A\u6570\u90FD\u662F\u5728\u8FD9\u4E2A\u6807\u51C6\u4E0B\u7684</p><p>\u5982\u679C\u6211\u4EEC\u81EA\u5DF1\u7F16\u5199\u652F\u6301\u8FD9\u4E2A\u6807\u51C6\u7684\u4EE3\u7801\u662F\u4E0D\u73B0\u5B9E\u7684,\u5FC5\u987B\u901A\u8FC7\u73B0\u6210\u7684\u6846\u67B6\u6216\u7EC4\u4EF6\u5B8C\u6210\u6EE1\u8DB3\u8FD9\u4E2A\u5FAE\u670D\u52A1\u6807\u51C6\u7684\u9879\u76EE\u7ED3\u6784\u548C\u683C\u5F0F</p><p>\u5F53\u4ECA\u7A0B\u5E8F\u5458\u8981\u60F3\u5FEB\u901F\u5B8C\u6210\u5FAE\u670D\u52A1\u6807\u51C6\u7684\u7A0B\u5E8F,\u9996\u9009SpringCloud</p><h1 id="spring-cloud" tabindex="-1"><a class="header-anchor" href="#spring-cloud" aria-hidden="true">#</a> Spring Cloud</h1><h2 id="\u4EC0\u4E48\u662Fspring-cloud" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fspring-cloud" aria-hidden="true">#</a> \u4EC0\u4E48\u662FSpring Cloud</h2><p>SpringCloud\u662F\u7531Spring\u63D0\u4F9B\u7684\u4E00\u5957\u80FD\u591F\u5FEB\u901F\u642D\u5EFA\u5FAE\u670D\u52A1\u67B6\u6784\u7A0B\u5E8F\u7684\u6846\u67B6\u96C6</p><p>\u6846\u67B6\u96C6\u8868\u793ASpringCloud\u4E0D\u662F\u4E00\u4E2A\u6846\u67B6,\u800C\u662F\u5F88\u591A\u6846\u67B6\u7684\u7EDF\u79F0</p><p>SpringCloud\u662F\u4E3A\u4E86\u642D\u5EFA\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u7A0B\u5E8F\u800C\u51FA\u73B0\u7684</p><p>\u6709\u4EBA\u5C06SpringCloud\u79F0\u4E4B\u4E3A&quot;Spring\u5168\u5BB6\u6876&quot;,\u5E7F\u4E49\u4E0A\u6307\u4EE3\u6240\u6709Spring\u7684\u4EA7\u54C1</p><h2 id="springcloud\u7684\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#springcloud\u7684\u5185\u5BB9" aria-hidden="true">#</a> SpringCloud\u7684\u5185\u5BB9</h2><p><strong>\u4ECE\u5185\u5BB9\u63D0\u4F9B\u8005\u89D2\u5EA6</strong></p><ul><li>Spring\u81EA\u5DF1\u7F16\u5199\u7684\u6846\u67B6\u548C\u8F6F\u4EF6</li><li>Netflix(\u5948\u975E):\u65E9\u671F\u63D0\u4F9B\u4E86\u5F88\u591A(\u5168\u5957)\u5FAE\u670D\u52A1\u67B6\u6784\u7EC4\u4EF6</li><li>alibaba(\u963F\u91CC\u5DF4\u5DF4):\u65B0\u7248\u672CSpringCloud\u63A8\u8350\u4F7F\u7528(\u6B63\u5728\u8FC5\u901F\u5360\u9886\u5E02\u573A)</li></ul><p>\u8BFE\u7A0B\u4E2D\u5927\u91CF\u4F7F\u7528alibaba\u7684\u5FAE\u670D\u52A1\u7EC4\u4EF6</p><p><strong>\u4ECE\u529F\u80FD\u4E0A\u5206\u7C7B</strong></p><ul><li>\u5FAE\u670D\u52A1\u7684\u6CE8\u518C\u4E2D\u5FC3</li><li>\u5FAE\u670D\u52A1\u95F4\u7684\u8C03\u7528</li><li>\u5FAE\u670D\u52A1\u7684\u5206\u5E03\u5F0F\u4E8B\u52A1</li><li>\u5FAE\u670D\u52A1\u7684\u9650\u6D41</li><li>\u5FAE\u670D\u52A1\u7684\u7F51\u5173</li><li>......</li></ul>',74),t=[d];function h(o,s){return p(),i("div",null,t)}var c=a(n,[["render",h],["__file","\u5FAE\u670D\u52A1\u6982\u5FF5.html.vue"]]);export{c as default};
